document.querySelectorAll('.faq-list__item').forEach(item => {
    item.addEventListener('click', () => {
        // Убираем класс у всех элементов
        document.querySelectorAll('.faq-list__item').forEach(el => el.classList.remove('faq-list__item--active'));
        // Добавляем класс текущему элементу
        item.classList.add('faq-list__item--active');
    });
});

const langElement = document.querySelector('.lang');

// Проверка на сенсорное устройство
const isTouchDevice = 'ontouchstart' in window || navigator.maxTouchPoints > 0;

if (isTouchDevice) {
    // Для мобильных устройств
    document.addEventListener('click', function (e) {
        if (langElement.contains(e.target)) {
            langElement.classList.toggle('lang--active');
        } else {
            langElement.classList.remove('lang--active');
        }
    });
} else {
    // Для десктопов
    langElement.addEventListener('mouseenter', function () {
        langElement.classList.add('lang--active');
    });

    langElement.addEventListener('mouseleave', function () {
        langElement.classList.remove('lang--active');
    });
}

// Aos - the right initialisation
jQuery(document).ready(function () {
    (function () {
        // your page initialization code here
        // the DOM will be available here
        AOS.init({
            duration: 750,
            offset: 0, // offset (in px) from the original trigger point
            anchorPlacement: 'top-bottom', // define where the AOS animations will be triggered
        });
    })();
});
